    /**
     * 随机类
     */
    public static class GT_Random {

        private final static Random random = new Random();

        /**
         * 获取一个未知整数
         *
         * @return int
         */
        public static int getInt() {
            int value = 0;
            try {
                int min = -2147483648;
                int max = 2147483647;
                value = random.nextInt(max) % (max - min + 1) + min;
            } catch (Exception e) {
                value = random.nextInt(1079);
            }
            return value;
        }

        /**
         * 获取随机范围的数
         *
         * @param min
         * @param max
         * @return
         */
        public static int getInt(int min, int max) {
            int value = random.nextInt(max);
            if (value < min) {
                return getInt(min, max);
            }
            return value;
        }

        /**
         * 获取定义字符之间的随机字符
         *
         * @param startChar
         * @param closeChar
         * @return
         */
        public static char getChar(char startChar, char closeChar) {
            return (char) (startChar + Math.random() * (closeChar - startChar + 1));
        }

        /**
         * 随机获取一个名称
         *
         * @return
         */
        public static String getName(int nameLength) {
            if (nameLength < 1) return null;
            String sum = "";
            for (int i = 1; i < nameLength + 1; i++) {
                String str = "";
                int hightPos; //
                int lowPos;
                hightPos = (176 + Math.abs(random.nextInt(39)));
                lowPos = (161 + Math.abs(random.nextInt(93)));
                byte[] b = new byte[2];
                b[0] = (Integer.valueOf(hightPos)).byteValue();
                b[1] = (Integer.valueOf(lowPos)).byteValue();
                try {
                    str = new String(b, "GBK");
                } catch (UnsupportedEncodingException e) {
                    e.printStackTrace();
                    System.out.println("错误");
                }
                sum += str.charAt(0);
            }
            return sum;
        }

    }

