        /***************************************************** 数据进制转换方法 *****************************************************/

        /**
         * 返回转换好的进制数据
         *
         * @param bytes 需要转换进制的数据
         * @return
         */
        public String returnTypeData(byte[] bytes) {
            String readData = "";
            switch (TYPE) {
                case 10:
                    readData = new String(bytes, Charset.forName("gbk"));
                    break;
                case 16:
                    int[] ints = DataUtils.byte2Int(bytes);
                    for (int value : ints) {
                        readData += value + ",";
                    }
                    break;
            }
            closeParameter();
            return readData;
        }

        /**
         * 释放资源
         */
        public void close() {
            if (inputStream != null) {
                try {
                    inputStream.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }

            if (outputStream != null) {
                try {
                    outputStream.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }

            if (serialPort != null) {
                serialPort.close();
                serialPort = null;
            }

            if (serialPortFinder != null) {
                serialPortFinder = null;
            }

        }

