    /**
     * 数据转换工具类
     */
    public static class DataUtils {

        /**
         * 16进制 byte 转10进制 int
         *
         * @param data
         * @return
         */
        public static int[] byte2Int(byte[] data) {
            int[] result = new int[data.length / 2];
            int value;
            int extra = 0;
            if (data.length % 2 != 0) {
                extra = 2;
            }
            for (int i = 0; i < data.length - extra; i += 2) {
                int int1 = (data[i] & 0xff) << 8;
                value = int1 + (data[i + 1] & 0xff);
                result[i / 2] = value;
            }
            return result;
        }

        // 判断奇数或偶数，位运算，最后一位是1则为奇数，为0是偶数
        public static int isOdd(int num) {
            return num & 1;
        }

        //Hex字符串转int
        public static int HexToInt(String inHex) {
            return Integer.parseInt(inHex, 16);
        }

        public static String IntToHex(int intHex) {
            return Integer.toHexString(intHex);
        }

        //Hex字符串转byte
        public static byte HexToByte(String inHex) {
            return (byte) Integer.parseInt(inHex, 16);
        }

        //1字节转2个Hex字符
        public static String Byte2Hex(Byte inByte) {
            return String.format("%02x", new Object[]{inByte}).toUpperCase();
        }

        //字节数组转转hex字符串
        public static String ByteArrToHex(byte[] inBytArr) {
            StringBuilder strBuilder = new StringBuilder();
            for (byte valueOf : inBytArr) {
                strBuilder.append(Byte2Hex(Byte.valueOf(valueOf)));
                strBuilder.append(" ");
            }
            return strBuilder.toString();
        }

        //字节数组转转hex字符串，可选长度
        public static String ByteArrToHex(byte[] inBytArr, int offset, int byteCount) {
            StringBuilder strBuilder = new StringBuilder();
            int j = byteCount;
            for (int i = offset; i < j; i++) {
                strBuilder.append(Byte2Hex(Byte.valueOf(inBytArr[i])));
            }
            return strBuilder.toString();
        }

        //转hex字符串转字节数组
        public static byte[] HexToByteArr(String inHex) {
            byte[] result;
            int hexlen = inHex.length();
            if (isOdd(hexlen) == 1) {
                hexlen++;
                result = new byte[(hexlen / 2)];
                inHex = "0" + inHex;
            } else {
                result = new byte[(hexlen / 2)];
            }
            int j = 0;
            for (int i = 0; i < hexlen; i += 2) {
                result[j] = HexToByte(inHex.substring(i, i + 2));
                j++;
            }
            return result;
        }

        /**
         * 按照指定长度切割字符串
         *
         * @param inputString 需要切割的源字符串
         * @param length      指定的长度
         * @return
         */
        public static List<String> getDivLines(String inputString, int length) {
            List<String> divList = new ArrayList<>();
            int remainder = (inputString.length()) % length;
            // 一共要分割成几段
            int number = (int) Math.floor((inputString.length()) / length);
            for (int index = 0; index < number; index++) {
                String childStr = inputString.substring(index * length, (index + 1) * length);
                divList.add(childStr);
            }
            if (remainder > 0) {
                String cStr = inputString.substring(number * length, inputString.length());
                divList.add(cStr);
            }
            return divList;
        }

        /**
         * 计算长度，两个字节长度
         *
         * @param val value
         * @return 结果
         */
        public static String twoByte(String val) {
            if (val.length() > 4) {
                val = val.substring(0, 4);
            } else {
                int l = 4 - val.length();
                for (int i = 0; i < l; i++) {
                    val = "0" + val;
                }
            }
            return val;
        }

        /**
         * 校验和
         *
         * @param cmd 指令
         * @return 结果
         */
        public static String sum(String cmd) {
            List<String> cmdList = getDivLines(cmd, 2);
            int sumInt = 0;
            for (String c : cmdList) {
                sumInt += HexToInt(c);
            }
            String sum = IntToHex(sumInt);
            sum = twoByte(sum);
            cmd += sum;
            return cmd.toUpperCase();
        }

        /**
         * 把中文转成Unicode码
         *
         * @param str
         * @return
         */
        public static String chinaToUnicode(String str) {
            String result = "";
            for (int i = 0; i < str.length(); i++) {
                int chr1 = (char) str.charAt(i);
                if (chr1 >= 19968 && chr1 <= 171941) {// 汉字范围 \u4e00-\u9fa5 (中文)
                    result += "\\u" + Integer.toHexString(chr1);
                } else {
                    result += str.charAt(i);
                }
            }
            return result;
        }

        /**
         * 判断是否为中文字符
         *
         * @param c
         * @return
         */
        public static boolean isChinese(char c) {
            Character.UnicodeBlock ub = Character.UnicodeBlock.of(c);
            if (ub == Character.UnicodeBlock.CJK_UNIFIED_IDEOGRAPHS
                    || ub == Character.UnicodeBlock.CJK_COMPATIBILITY_IDEOGRAPHS
                    || ub == Character.UnicodeBlock.CJK_UNIFIED_IDEOGRAPHS_EXTENSION_A
                    || ub == Character.UnicodeBlock.GENERAL_PUNCTUATION
                    || ub == Character.UnicodeBlock.CJK_SYMBOLS_AND_PUNCTUATION
                    || ub == Character.UnicodeBlock.HALFWIDTH_AND_FULLWIDTH_FORMS) {
                return true;
            }
            return false;
        }

        //Unicode转中文
        public static String decodeUnicode(final String unicode) {
            StringBuffer string = new StringBuffer();

            String[] hex = unicode.split("\\\\u");

            for (int i = 0; i < hex.length; i++) {

                try {
                    // 汉字范围 \u4e00-\u9fa5 (中文)
                    if (hex[i].length() >= 4) {//取前四个，判断是否是汉字
                        String chinese = hex[i].substring(0, 4);
                        try {
                            int chr = Integer.parseInt(chinese, 16);
                            boolean isChinese = isChinese((char) chr);
                            //转化成功，判断是否在  汉字范围内
                            if (isChinese) {//在汉字范围内
                                // 追加成string
                                string.append((char) chr);
                                //并且追加  后面的字符
                                String behindString = hex[i].substring(4);
                                string.append(behindString);
                            } else {
                                string.append(hex[i]);
                            }
                        } catch (NumberFormatException e1) {
                            string.append(hex[i]);
                        }

                    } else {
                        string.append(hex[i]);
                    }
                } catch (NumberFormatException e) {
                    string.append(hex[i]);
                }
            }

            return string.toString();
        }

        /**
         * 将输入流转换成字符串
         *
         * @param is 从网络获取的输入流
         * @return
         */
        public static String streamToString(InputStream is) {
            try {
                ByteArrayOutputStream baos = new ByteArrayOutputStream();
                byte[] buffer = new byte[1024];
                int len = 0;
                while ((len = is.read(buffer)) != -1) {
                    baos.write(buffer, 0, len);
                }
                baos.close();
                is.close();
                byte[] byteArray = baos.toByteArray();
                return new String(byteArray);
            } catch (Exception e) {
                GT.errs(e.toString());
                return null;
            }
        }

    }

