            /**
             * 用于一次性刷新加载数据 (只需要加载一次)
             *
             * @param beanList
             */
            public void setBeanList(List<T> beanList, int... pages) {
                if (beanList == null) return;

                if (pages.length > 0) {
                    page = pages[0];
                }
                switch (page) {
                    case -1://直接赋值，仅用于只需要加载一次的情况下
                        this.beanList = beanList;
                        break;
                    case 0://第一页
                        this.beanList.clear();
                        notifyDataSetChanged();
                        this.beanList.addAll(beanList);
                        break;
                }
                notifyDataSetChanged();
//                if(rv != null)rv.setItemViewCacheSize(getItemCount());
            }

            /**
             * 用于累计刷新加载数据 (分页加载)
             *
             * @param beanList
             */
            public void setBeanLists(List<T> beanList, int... pages) {
                if (beanList == null) return;
                if (pages.length > 0) {
                    page = pages[0];
                }
                switch (page) {
                    case -1://直接赋值，仅用于只需要加载一次的情况下
                        this.beanList = beanList;
                        break;
                    case 1://第一页
                        this.beanList.clear();
                        notifyDataSetChanged();
                        this.beanList.addAll(beanList);
                        break;
                    default://累加分页
                        if (this.beanList == null) {
                            this.beanList = beanList;
                        } else {
                            this.beanList.addAll(this.beanList.size(), beanList);
                        }
                        break;
                }
                notifyDataSetChanged();
//                if(rv != null)rv.setItemViewCacheSize(getItemCount());
            }


